import { Button, Form, Image, Input } from 'antd';
import { useEffect } from 'react';
import { history } from 'umi';

export default function () {
  const [form] = Form.useForm();

  const title = Form.useWatch<string>('title', form);
  const content = Form.useWatch<string>('content', form);
  const imageUrl = Form.useWatch<string>('imageUrl', form);
  const tags = Form.useWatch<string>('tags', form);

  useEffect(() => {
    if (!document.cookie.includes('token')) {
      alert('请先登录!');
      history.push('/login');
    }
  }, []);

  async function submit() {
    console.log(tags);
    try {
      const res = await fetch('/api/posts', {
        method: 'POST',
        body: JSON.stringify({
          title,
          content,
          tags: tags.split(','),
          imageUrl,
        }),
        headers: {
          'Content-Type': 'application/json',
        },
      });
      if (res.status !== 200) {
        console.error(await res.text());
        alert('发布失败');
        return;
      }
      history.push('/posts/' + (await res.json()).id);
    } catch (err) {
      console.error(err);
    }
  }

  return (
    <div className="w-full flex justify-center">
      <div className="container lg:px-64 px-8 pt-16">
        <p className="text-3xl font-extrabold">发表新文章</p>
        <Form
          name="basic"
          form={form}
          initialValues={{ remember: true }}
          autoComplete="off"
          onFinish={submit}
          size="large"
        >
          <Form.Item
            label="标题"
            name="title"
            rules={[{ required: true, message: '请输入标题!' }]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            label="内文"
            name="content"
            rules={[{ required: true, message: '请输入内文!' }]}
          >
            <Input.TextArea showCount autoSize />
          </Form.Item>
          <Form.Item
            label="标签(以逗号隔开)"
            name="tags"
            rules={[{ required: true, message: '请输入标签!' }]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            label="封面图片地址"
            name="imageUrl"
            rules={[{ required: true, message: '请输入封面图片地址!' }]}
          >
            <Input />
          </Form.Item>

          <Form.Item>
            <Image
              width={200}
              src={
                imageUrl
                  ? imageUrl
                  : 'data:image/png;base64,'
              }
            />
          </Form.Item>

          <Form.Item>
            <Button className="mr-8" type="primary" htmlType="submit">
              发布
            </Button>
          </Form.Item>
        </Form>
      </div>
    </div>
  );
}
