import {history} from "@@/core/history";
import {Button, Image, Form, Input} from "antd";


export default function () {
    const [form] = Form.useForm();
    const email = Form.useWatch('email', form);
    const password = Form.useWatch('password', form);
    const name = Form.useWatch('name', form);
    const avatarUrl = Form.useWatch('avatarUrl', form);

    async function submit() {
        try {
            const res = await fetch('/api/register', {
                method: 'POST',
                body: JSON.stringify({
                    email: email,
                    password: password,
                    name: name,
                    avatarUrl: avatarUrl
                }),
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            if (res.status !== 200) {
                console.error(await res.text());
                return;
            }
            const data = await res.json();
            alert(`注册成功, ${data.name}`);
            history.push('/create');
        } catch (err) {
            console.error(err);
        }
    }

    return <div className="w-full flex justify-center">
        <div className="container lg:px-64 px-8 pt-16">
            <p className="text-3xl font-extrabold">用户注册</p>
            <div className="mt-8">
                {/*<p>邮箱</p>*/}
                {/*<TextInput value={email} onChange={setEmail}/>*/}
                {/*<p className="mt-4">用户名</p>*/}
                {/*<TextInput value={name} onChange={setName}/>*/}
                {/*<p className="mt-4">密码</p>*/}
                {/*<TextInput type="password" value={password} onChange={setPassword}/>*/}
                {/*<p className="mt-4">头像</p>*/}
                {/*<TextInput value={avatarUrl} onChange={setAvatarUrl}/>*/}
                {/*<img src={avatarUrl} alt=""/>*/}
                {/*<CustomButton onClick={submit}>注册</CustomButton>*/}
                <Form
                    name="basic"
                    form={form}
                    initialValues={{remember: true}}
                    autoComplete="off"
                    onFinish={submit}
                    size="large"
                >
                    <Form.Item
                        label="邮箱"
                        name="email"
                        rules={[{required: true, message: 'Please input your username!'}]}
                    >
                        <Input/>
                    </Form.Item>

                    <Form.Item
                        label="密码"
                        name="password"
                        rules={[{required: true, message: 'Please input your password!'}]}
                    >
                        <Input.Password/>
                    </Form.Item>

                    <Form.Item
                        label="昵称"
                        name="username"
                        rules={[{required: true, message: 'Please input your name!'}]}
                    >
                        <Input/>
                    </Form.Item>

                    <Form.Item
                        label="头像"
                        name="avatarUrl"
                        rules={[{required: true, message: 'Please input your avatar!'}]}
                    >
                        <Input/>

                    </Form.Item>

                    <Form.Item>
                        <Image width={200}
                               src={avatarUrl ? avatarUrl : "data:image/png;base64,"}/>
                    </Form.Item>

                    <Form.Item>
                        <Button className="mr-8" size="large" type="primary" htmlType="submit">
                            注册
                        </Button>
                    </Form.Item>
                </Form>
            </div>
        </div>
    </div>;
}
